import React, { useEffect, useState } from 'react';
import './WcagToggle.css'; // Import the CSS file

const WcagToggle = () => {
  const [isVisible, setIsVisible] = useState(false); // State to manage visibility

  const toggleVisibility = () => {
    setIsVisible(prev => !prev); // Toggle visibility state
  };

  return (
    <div className={`wcag-container ${isVisible ? 'show' : ''}`}>
      <button 
        className="aside-wcag__toggle" 
        onClick={toggleVisibility} 
        aria-expanded={isVisible}
      >
        Toggle WCAG
      </button>
      <div className="wcag-btt">
        <div className="wcag-options">
          <button className="wcag-btt__text-normal">Normal Text</button>
          <button className="wcag-btt__text-plus">Larger Text</button>
          <button className="wcag-btt__text-plus-plus">Largest Text</button>
          <button className="wcag-btt__wcag-hi">High Contrast</button>
          <button className="wcag-btt__wcag-lo">Low Contrast</button>
        </div>
      </div>
    </div>
  );
};

export default WcagToggle;
