import React, { useEffect } from 'react';

const WcagToggle = () => {
  const setCookie = (cname, cvalue, exdays) => {
    const d = new Date();
    d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
    const expires = "expires=" + d.toUTCString();
    document.cookie = `${cname}=${cvalue}; ${expires}; path=/`;
  };

  const getCookie = (cname) => {
    const name = cname + "=";
    const ca = document.cookie.split(';');
    for (let i = 0; i < ca.length; i++) {
      let c = ca[i];
      while (c.charAt(0) === ' ') {
        c = c.substring(1);
      }
      if (c.indexOf(name) === 0) {
        return c.substring(name.length, c.length);
      }
    }
    return "";
  };

  const toggleClass = (className, cookieName) => {
    const body = document.body;
    if (body.classList.contains(className)) {
      body.classList.remove(className);
      setCookie(cookieName, 0, 1);
    } else {
      body.classList.add(className);
      setCookie(cookieName, 1, 1);
    }
  };

  const handleTextNormal = () => {
    toggleClass('wcag__text-normal', 'wcagTextNormal');
    document.body.classList.remove('wcag__text-plus', 'wcag__text-plus-plus');
    setCookie('wcagTextPlus', 0, 1);
    setCookie('wcagTextPlusPlus', 0, 1);
  };

  const handleTextPlus = () => {
    toggleClass('wcag__text-plus', 'wcagTextPlus');
    document.body.classList.remove('wcag__text-normal', 'wcag__text-plus-plus');
    setCookie('wcagTextNormal', 0, 1);
    setCookie('wcagTextPlusPlus', 0, 1);
  };

  const handleTextPlusPlus = () => {
    toggleClass('wcag__text-plus-plus', 'wcagTextPlusPlus');
    document.body.classList.remove('wcag__text-normal', 'wcag__text-plus');
    setCookie('wcagTextNormal', 0, 1);
    setCookie('wcagTextPlus', 0, 1);
  };

  const handleContrastToggle = () => {
    toggleClass('wcag__contrast', 'wcagContrast');
  };

  useEffect(() => {
    if (getCookie('wcagTextNormal') === '1') {
      document.body.classList.add('wcag__text-normal');
    }
    if (getCookie('wcagTextPlus') === '1') {
      document.body.classList.add('wcag__text-plus');
    }
    if (getCookie('wcagTextPlusPlus') === '1') {
      document.body.classList.add('wcag__text-plus-plus');
    }
    if (getCookie('wcagContrast') === '1') {
      document.body.classList.add('wcag__contrast');
    }
  }, []);

  return (
    <div className="aside-wcag">
      <button className="aside-wcag__toggle" aria-expanded="false">
        Toggle WCAG
      </button>
      <div className="wcag-btt">
        <button className="wcag-btt__text-normal" onClick={handleTextNormal}>
          Normal Text
        </button>
        <button className="wcag-btt__text-plus" onClick={handleTextPlus}>
          Larger Text
        </button>
        <button className="wcag-btt__text-plus-plus" onClick={handleTextPlusPlus}>
          Largest Text
        </button>
        <button className="wcag-btt__wcag-hi" onClick={handleContrastToggle}>
          High Contrast
        </button>
        <button className="wcag-btt__wcag-lo" onClick={handleContrastToggle}>
          Low Contrast
        </button>
      </div>
    </div>
  );
};

export default WcagToggle;
