import React, { useState } from 'react';
import './WcagToggle.css';
import { Accessibility } from 'lucide-react';

const WcagToggle = () => {
  const [isVisible, setIsVisible] = useState(false);

  const toggleVisibility = () => {
    setIsVisible(prev => !prev);
  };

  return (
    <>
      <div className={`wcag-container ${isVisible ? 'show' : ''}`}>
        <div className="wcag-btt">
          <button 
            className="aside-wcag__toggle" 
            onClick={toggleVisibility} 
            aria-expanded={isVisible}>
              <Accessibility />
          </button>
          <div className="wcag-options">
            <button>Text</button>
            <button>Text+</button>
            <button>Text++</button>
            <button>High Contrast</button>
            <button>Low Contrast</button>
            <button>Reset Settings</button>
            <button>Accessibility declaration</button>
            <button>Sitemap</button>
          </div>
        </div>
      </div>
    </>
  );
};

export default WcagToggle;
