import React, { useEffect, useState } from 'react';
import './WcagToggle.css'; // Import the CSS file

const WcagToggle = () => {
  const [isVisible, setIsVisible] = useState(false); // State to manage visibility

  const toggleVisibility = () => {
    setIsVisible(prev => !prev); // Toggle visibility state
  };

  return (
    <div className={`wcag-btt ${isVisible ? 'show' : ''}`}>
      <button 
        className="aside-wcag__toggle" 
        onClick={toggleVisibility} 
        aria-expanded={isVisible}
      >
        Toggle WCAG
      </button>
      <div className="wcag-options">
        <button className="wcag-btt__text-normal" onClick={() => { /* Handle Normal Text */ }}>
          Normal Text
        </button>
        <button className="wcag-btt__text-plus" onClick={() => { /* Handle Larger Text */ }}>
          Larger Text
        </button>
        <button className="wcag-btt__text-plus-plus" onClick={() => { /* Handle Largest Text */ }}>
          Largest Text
        </button>
        <button className="wcag-btt__wcag-hi" onClick={() => { /* Handle High Contrast */ }}>
          High Contrast
        </button>
        <button className="wcag-btt__wcag-lo" onClick={() => { /* Handle Low Contrast */ }}>
          Low Contrast
        </button>
      </div>
    </div>
  );
};

export default WcagToggle;
