import React, { useState } from 'react';
import './WcagToggle.css';
import { Accessibility } from 'lucide-react';

const WcagToggle = () => {
  const [isVisible, setIsVisible] = useState(false);

  const toggleVisibility = () => {
    setIsVisible(prev => !prev);
  };

  return (
    <>
      {/* Test div to check if component renders */}
      <div style={{ position: 'fixed', top: '10px', left: '10px', background: 'red', color: 'white', zIndex: 9999, padding: '5px' }}>
        WCAG Component Loaded
      </div>

      <div className={`wcag-container ${isVisible ? 'show' : ''}`}>
        <div className="wcag-btt">
          <button 
            className="aside-wcag__toggle" 
            onClick={toggleVisibility} 
            aria-expanded={isVisible}
            style={{ 
              position: 'fixed', 
              top: '50px', 
              left: '50px', 
              background: 'blue', 
              color: 'white', 
              padding: '10px',
              zIndex: 10000,
              border: 'none',
              cursor: 'pointer'
            }}>
              <Accessibility />
          </button>
          <div className="wcag-options">
            <button>Text</button>
            <button>Text+</button>
            <button>Text++</button>
            <button>High Contrast</button>
            <button>Low Contrast</button>
            <button>Reset Settings</button>
            <button>Accessibility declaration</button>
            <button>Sitemap</button>
          </div>
        </div>
      </div>
    </>
  );
};

export default WcagToggle;
