'use client'; // This makes the component a Client Component

import { useEffect, useState } from 'react';

function ReviewsPage() {
    const [reviews, setReviews] = useState([]);
    const [error, setError] = useState(null);
    const placeId = "ChIJl0Rtw56fFkcR9iJVhqb61rg"; // Replace with your actual Place ID
    const apiKey = "AIzaSyCV7mddTzz-yu_11-FZtxIim3iunxUiZdg"; // Replace with your actual API Key

    useEffect(() => {
        const fetchReviews = async () => {
            try {
                const response = await fetch(`https://maps.googleapis.com/maps/api/place/details/json?place_id=${placeId}&key=${apiKey}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setReviews(data.result.reviews); // Adjust based on the API response structure
            } catch (error) {
                console.error("Failed to fetch reviews:", error);
                setError(error.message);
            }
        };

        fetchReviews();
    }, [placeId, apiKey]);

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h1>Reviews</h1>
            {reviews.length > 0 ? (
                reviews.map((review) => (
                    <div key={review.author_name}>
                        <h2>{review.author_name}</h2>
                        <p>{review.text}</p>
                    </div>
                ))
            ) : (
                <p>No reviews found.</p>
            )}
        </div>
    );
}

export default ReviewsPage;
